import React, { useState, useEffect } from 'react';
import api from '../services/api';

const Todos = () => {
  const [todos, setTodos] = useState([]);
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = async () => {
    try {
      const response = await api.getTodos();
      setTodos(response.data);
    } catch (error) {
      alert('Failed to fetch todos');
    }
  };

  const handleAddTodo = async (e) => {
    e.preventDefault();
    try {
      await api.addTodo(description, status);
      fetchTodos();
      setDescription('');
      setStatus('');
    } catch (error) {
      alert('Failed to add todo');
    }
  };

  const handleUpdateTodo = async (id, newDescription, newStatus) => {
    try {
      await api.updateTodo(id, newDescription, newStatus);
      fetchTodos();
    } catch (error) {
      alert('Failed to update todo');
    }
  };

  const handleDeleteTodo = async (id) => {
    try {
      await api.deleteTodo(id);
      fetchTodos();
    } catch (error) {
      alert('Failed to delete todo');
    }
  };

  return (
    <div>
      <h2>Todos</h2>
      <form onSubmit={handleAddTodo}>
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="text"
          placeholder="Status"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        />
        <button type="submit">Add Todo</button>
      </form>
      <ul>
        {todos.map(todo => (
          <li key={todo.id}>
            <span>{todo.description}</span>
            <span>{todo.status}</span>
            <button onClick={() => handleUpdateTodo(todo.id, 'Updated Description', 'Updated Status')}>Update</button>
            <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Todos;
